--Finds all orders where a 735-3040 or a 734-351 are present.
SELECT dtl.ORDER_ID, hdr.PATIENT_ID, hdr.CLIENT_ID, hdr.ORDER_DATETIME, dtl.PRODUCT_LINE_ID, dtl.PRODUCT_SEQ_ID, prod.LONG_DESCRIPTION FROM UVIS_PRODUCT prod, UVIS_ORDER_DTL dtl, UVIS_ORDER_HDR hdr WHERE ((dtl.PRODUCT_LINE_ID = '735' AND dtl.PRODUCT_SEQ_ID = '3040') OR (dtl.PRODUCT_LINE_ID = '734' AND dtl.PRODUCT_SEQ_ID = '351')) AND dtl.ORDER_ITEM_STATUS = 'FN' AND dtl.ORDER_ID = hdr.ORDER_ID AND dtl.ORDER_ID_SUFFIX = hdr.ORDER_ID_SUFFIX AND dtl.PRODUCT_LINE_ID = prod.PRODUCT_LINE_ID AND dtl.PRODUCT_SEQ_ID = prod.PRODUCT_SEQ_ID;

--Finds all cases where a 734-1221 was ordered as long as it was accompanied by either a 734-1100 or a 734-1105.
SELECT ORDER_ID, PATIENT_ID, CLIENT_ID, ORDER_DATETIME, PRODUCT_LINE_ID, PRODUCT_SEQ_ID, LONG_DESCRIPTION, PRODUCT_LINE_ID2, PRODUCT_SEQ_ID2, LONG_DESCRIPTION2 FROM 

(SELECT dtl.ORDER_ID, hdr.PATIENT_ID, hdr.CLIENT_ID, hdr.ORDER_DATETIME, dtl.PRODUCT_LINE_ID, dtl.PRODUCT_SEQ_ID, prod.LONG_DESCRIPTION FROM UVIS_PRODUCT prod, UVIS_ORDER_DTL dtl, UVIS_ORDER_HDR hdr WHERE (dtl.PRODUCT_LINE_ID = '734' AND dtl.PRODUCT_SEQ_ID = '1221') AND dtl.ORDER_ITEM_STATUS = 'FN' AND dtl.ORDER_ID = hdr.ORDER_ID AND dtl.ORDER_ID_SUFFIX = hdr.ORDER_ID_SUFFIX AND dtl.PRODUCT_LINE_ID = prod.PRODUCT_LINE_ID AND dtl.PRODUCT_SEQ_ID = prod.PRODUCT_SEQ_ID)

INNER JOIN (SELECT ORDER_ID AS ORDER_ID2, dtl.PRODUCT_LINE_ID AS PRODUCT_LINE_ID2, dtl.PRODUCT_SEQ_ID AS PRODUCT_SEQ_ID2, prod.LONG_DESCRIPTION AS LONG_DESCRIPTION2 FROM UVIS_ORDER_DTL dtl, UVIS_PRODUCT prod WHERE ((dtl.PRODUCT_LINE_ID = '734' AND dtl.PRODUCT_SEQ_ID = '1100') OR (dtl.PRODUCT_LINE_ID = '734' AND dtl.PRODUCT_SEQ_ID = '1105')) AND ORDER_ITEM_STATUS = 'FN' AND dtl.PRODUCT_LINE_ID = prod.PRODUCT_LINE_ID AND dtl.PRODUCT_SEQ_ID = prod.PRODUCT_SEQ_ID) ON ORDER_ID = ORDER_ID2;

