INNER JOIN (SELECT ORDER_ID AS ORDER_ID2, dtl.PRODUCT_LINE_ID AS PRODUCT_LINE_ID2, dtl.PRODUCT_SEQ_ID AS PRODUCT_SEQ_ID2, prod.LONG_DESCRIPTION AS LONG_DESCRIPTION2 FROM UVIS_ORDER_DTL dtl, UVIS_PRODUCT prod WHERE ((dtl.PRODUCT_LINE_ID = '312' AND dtl.PRODUCT_SEQ_ID = '4290') OR (dtl.PRODUCT_LINE_ID = '312' AND dtl.PRODUCT_SEQ_ID = '4291')) AND dtl.ORDER_ITEM_STATUS = 'FN' AND dtl.PRODUCT_LINE_ID = prod.PRODUCT_LINE_ID AND dtl.PRODUCT_SEQ_ID = prod.PRODUCT_SEQ_ID) ON ORDER_ID = ORDER_ID2;